import React from "react";
import { NavLink } from "react-router-dom";
import { Menu, Button } from 'semantic-ui-react'

class NavBar extends React.Component {
    state = {}

   handleItemClick = (e, { name }) => this.setState({ activeItem: name })


   clickHandler = () => {
    localStorage.clear()
    this.props.clearUser()
    this.props.history.push('/')
  }


  render(){
    console.log(this.props.user)
    const { activeItem } = this.state

    return (
      <div class="ui inverted menu">
              <NavLink exact to="/" className="ui item" activeClassName="ui active item">
              Home
              </NavLink>

              <NavLink activeClassName="ui active item" className="ui item" to="/about">
              About
              </NavLink>

            {this.props.user ? (
              <React.Fragment>
                <span className="ui item">Logged in as: {this.props.user.name}</span>
                <Button onClick={this.clickHandler}>Logout</Button>
                </React.Fragment>
            ) : (
              <React.Fragment>
                <NavLink exact to="/signup" className="ui item" activeClassName="ui active item">SignUp</NavLink>
                <NavLink exact to="/login" className="ui item" activeClassName="ui active item">Login</NavLink>
              </React.Fragment>
            )}

      </div>
      );
  }
};

export default NavBar;



