import React from "react";

import { Form, Button, Modal } from 'semantic-ui-react'



class NewPostForm extends React.Component{
  constructor(){
    super()
    this.state={
      content: "",
      url: ""
    }
  }


   handleSubmit = (e) => {
      e.preventDefault();
      console.log("new post")
