import React from "react";

import GigListItem  from './EventListItem'
import { connect } from "react-redux";

import { Grid, Loader, List, Button } from 'semantic-ui-react'

//adjust this map so it goes through the events you are included on
const GigsList = (props) => (
    console.log(props.events.map(event => event.users.filter( user => user === props.currentUser))),
      <React.Fragment>
        {props.loading ?
        <Loader active inline='centered' />
      :
        <List>
          {props.currentUser ?
          props.events.filter(event => event.users.includes(props.currentUser)).map(gig =>
           <List.Item><GigListItem gig={gig} /></List.Item>
         ) : null }
       </List>}
      </React.Fragment>
);

const mapStateToProps = state => {
  return {
    loading: state.loading,
    events: state.events
  };
};


export default connect(mapStateToProps)(GigsList);
