class App extends Component {
  // constructor() {
  //   super();
  //   this.state = {
  //     user: null,
  //     allUsers: [],
  //     events: []
  //   };
  // }


  componentDidMount() {
    if (localStorage.getItem("token")) {
      this.props.getCurrentUser(); //dispatch this function from actions
    }
  }




