//find simple way to check if there is a valid url (maybe npm package) before rendering

  return(
    console.log(props.post.url),
    <React.Fragment>
        {props.post.url !== "" ?
          <div>
          Notes: <p>{props.post.content}</p>
          <iframe id="player"
            type="text/html"
            width="640"
            height="390"
            src={props.post.url}
            frameborder="0"></iframe>
          </div> :








