import { connect } from "react-redux";
import { List, Button, Form, Modal } from 'semantic-ui-react'

import { fetchedEvents } from "../redux/actions/index";



class AddUserToEvent extends React.Component {
  constructor(props){
    super(props)
    this.state={}
  }

render(){
  console.log(this.psop)
  return(
  <Modal trigger={<Button>Add Player</Button>}>
    <Form>
    <List>
      {this.props.currentUser ?
      this.props.events.filter(event => event.user_id === this.props.currentUser.id ).map(event =>
       <List.Item> event.name /></List.Item>
     ) : null}
   </List>}
    </Form>
  </Modal>

  )
  }
}

const mapStateToProps = state => {
  return {
    loading: state.loading,
    events: state.events
  };
};




export default connect(mapStateToProps)(AddUserToEvent);
