import React, { Component } from "react";
import { Route, Switch } from "react-router-dom";
import "./App.css";

import NavBar from "./components/NavBar";
import About from "./components/About";
import Login from "./components/Login";
import UsersContainer from "./components/UsersContainer";
import EventsPage from "./components/EventsPage";
import SearchBar from "./components/SearchBar";


const requestHelper = url =>
  fetch(url, {
    method: "GET",
    headers: {
      Authorization: `Bearer ${localStorage.getItem("token")}`
    }
  }).then(res => {
    if (res.status === 401) {
      alert("login failed");
    } else {
      return res.json();
    }
  });

class App extends Component {
  constructor() {
    super();
    this.state = {
      user: null,
      allUsers: [],
      events: []
    };
  }


  componentDidMount() {
    if (localStorage.getItem("token")) {
      this.fetchUser();
    }
  }




  clearUser =()=>{
    this.setState({user: null})
  }


  fetchUsers = () => {
    fetch(`http://localhost:3001/users`)
      .then(response => response.json())
      .then(allUsers => {
        this.setState({ allUsers });
      });
  };



  fetchUser = () => {
   requestHelper("http://localhost:3001/profile").then(this.updateUser);
 };



 updateUser = user => {
   this.setState({ user });
 };




  render() {
    return (
      <div className="App">
        <Route path='/' render={(props)=><NavBar
          title="Unison"
          color="#282c34"
          subtitle="Users Network Invite Schedule Organize Notes"
          clearUser= {this.clearUser}
          user={this.state.user} /> } />
        <Switch>
          <Route
            path="/login"
            render={(props) => <Login updateUser={this.updateUser} />}
          />
        <Route path="/about" component={About} />
        <Route path="/myevents"
          render={(props) => <EventsPage
            events={this.state.events}
            user={this.state.user}
            fetchEvents={this.fetchEvents}
            />}
          />

        <Route
            exact path="/"
            render={(props) => {
              return (
                <React.Fragment>
                  <SearchBar />
                  <UsersContainer
                  allUsers={this.state.allUsers}
                  />
                 </React.Fragment>
               );
             }}
             />
           </Switch>
      </div>
    );
  }
}

export default App;
