import React from "react";
import { NavLink } from "react-router-dom";

const NavBar = props => {
  return (
    <div className={`ui inverted ${props.color} menu navbar`}>
      <NavLink activeClassName="ui active item" className="ui item" to="/about">
        About
      </NavLink>
      <NavLink
        exact
        to="/"
        className="ui item"
        activeClassName="ui active item"
      >
        Home
      </NavLink>
      {props.user ? (
        <span className="ui item">Logged in as: {props.user.name}</span>
      ) : (
        <NavLink
          exact
          to="/login"
          className="ui item"
          activeClassName="ui active item"
        >
          Login
        </NavLink>
      )}
    </div>
  );
};

export default NavBar;
