import React from 'react';
import { Dropdown } from 'semantic-ui-react'


class PickEvent extends React.Component {
  constructor(props){
    super(props)
    this.state={
      value:{
        text: '',
        value: ''
      }
    }
  }

handleChange = (e, { value }) => {
  this.setState({ value })
  return this.state.value
}

///need to find a way to capture state of this dropdown and place into this callback
// props.filterBySort = (this.state.value) => {
//
// }


render(){
  const { value } = this.state
  const choices= [
    {
      text: 'Price Low to High',
      value: 'Price Low to High'
    },
    {
      text: 'Price High to Low',
      value: 'Price High to Low'
    },
    {
      text: 'Condition',
      value: 'Condition'
    },
    {
      text: 'Brand',
      value: 'Brand'
    }
  ]
  return(
    <React.Fragment>
        <Dropdown
          fluid selection
          size='small'
          onChange={(e, { value })=>this.props.filterBySort(e, { value })}
          placeholder='Sort By'
          name="choices"
          options={choices}
        />
    </React.Fragment>
  )}
}

export default PickEvent;
