import React from "react";
import { Grid } from 'semantic-ui-react';
import { connect } from "react-redux";

import Iframe from 'react-iframe';



class Post extends React.Component{


renderIframe = (params) => {
    switch (this.props.post.url.includes(params)){
       case this.props.post.url.includes('pdf'):
       return (
          <div>
          Notes: <p>{this.props.post.content}</p>
            <iframe
              style="border:1px solid #666CCC"
              title="PDF in an i-Frame"
              src={this.props.post.url}
              frameborder="1"
              scrolling="auto"
              height="1100"
              width="850" ></iframe>
            </div>
          )
            break;
      case this.props.post.url.includes('youtube'):
      return (
          <div>
          Notes: <p>{this.props.post.content}</p>
          <iframe width="380"
            height="157"
            src={"https://www.youtube.com/embed/"+ this.props.post.url.split("/")[3]}
            frameborder="0"
            allow="autoplay; encrypted-media"
            allowfullscreen></iframe>
            </div>
          )
            break;
      default:
      return (
          <div>
            <p>{this.props.post.content}</p>
          </div>
        )
        }
      }

render(){
  return(
    console.log(this.props.post),
    <React.Fragment>
        {this.renderIframe(props.post.url)}
     </React.Fragment>
  )
}

}

