render(){
  const { value } = this.props.events.map


  const choices= [
    {
      text: 'Price Low to High',
      value: 'Price Low to High'
    },
    {
      text: 'Price High to Low',
      value: 'Price High to Low'
    },
    {
      text: 'Condition',
      value: 'Condition'
    },
    {
      text: 'Brand',
      value: 'Brand'
    }
  ]

  return(
    <React.Fragment>
        <Dropdown
          fluid selection
          size='small'
          onChange={(e, { value })=> console.log('add user')}
          placeholder='Choose Event'
          name="choices"
          options={choices}
        />
    </React.Fragment>
  )}
}


const mapStateToProps = state => {
  return {
    loading: state.loading,
    events: state.events
  };
};




export default connect(mapStateToProps)(PickEvent);
