//       alert("login failed");
//     } else {
//       return res.json();
//     }
//   });

class App extends Component {
  // constructor() {
  //   super();
  //   this.state = {
  //     user: null,
  //     allUsers: [],
  //     events: []
  //   };
  // }


  componentDidMount() {
    if (localStorage.getItem("token")) {
      this.props.getCurrentUser(); //dispatch this function from actions
    }
  }




  clearUser =()=>{
    this.setState({user: null})
  }






  fetchUser = () => {
   requestHelper("http://localhost:3001/profile").then(this.updateUser);
 };



 updateUser = user => {
   this.setState({ user });
 };




  render() {
    return (
      <div className="App">
        <Route path='/' render={(props)=><NavBar
          title="Unison"
          color="#282c34"
          subtitle="Users Network Invite Schedule Organize Notes"
         /> } />
        <Switch>
          <Route
            path="/login"
            render={(props) => <Login updateUser={this.updateUser} />}
          />
        <Route path="/about" component={About} />
        <Route path="/myevents" component={EventsPage} />
        <Route
           exact path="/"
            render={(props) => {
              return (
                <React.Fragment>
                  <SearchBar />
                  <UsersContainer/>
                 </React.Fragment>
               );
             }}
             />
          </Switch>
      </div>
    );
  }
}


const mapStateToProps = state => {
  console.log(state)
  return {
    loading: state.loading,
    currentUser: state.currentUser
  };
};


export default connect(
  mapStateToProps,
  { getCurrentUser }
)(App);
