import React from "react";

import GigListItem  from './EventListItem'
import { connect } from "react-redux";

import { Grid, Loader, List, Button } from 'semantic-ui-react'

//adjust this map so it goes through the events you are included on
const GigsList = (props) => (
      <React.Fragment>
        {props.loading ?
        <Loader active inline='centered' />
      :
        <List>
          {props.currentUser ?
          props.events.filter(event => event.user_id === props.currentUser.id ).map(event =>
           <List.Item><GigListItem key={gig.id} gig={gig} /></List.Item>
         ) : null}
       </List>}
      </React.Fragment>
);

const mapStateToProps = state => {
  return {
    loading: state.loading,
    events: state.events
  };
};


export default connect(mapStateToProps)(GigsList);
