import React from 'react';
import { connect } from "react-redux";
import { Grid, Card, Button, Form, Modal } from 'semantic-ui-react'


class AddUserToEvent extends React.Component {
  constructor(){
    super()
    this.state={}
  }

render(){
  return(
  <Modal trigger={<Button>Add Player</Button>}>
    <Form>

    <List>
      {this.props.currentUser.events.map( event =>
        <List.Item>event.name</List.Item>
      )}
    </List>

    </Form>
  </Modal>

  )
  }
}



export default AddUserToEvent;
