import React from "react";
import { Link, Route, Switch  } from "react-router-dom";
import { connect } from "react-redux";
import { fetchEvents, fetchPosts, loading } from "../redux/actions/index";

import { Grid, Image, Loader, Button } from 'semantic-ui-react';
import PlayersList from './PlayersList';
// import PostList from './PostList';

import DatePicker from 'react-datepicker';
import moment from 'moment';
import 'react-datepicker/dist/react-datepicker.css';





class GigDetail extends React.Component {

componentDidMount(){
  this.props.fetchEvents();
  this.props.fetchPosts();
}


  render() {
    console.log(this.props.gig.event)
      return (
        <React.Fragment>
           {this.props.loading ?
              <Loader active inline='centered' />
             :
        <Grid columns={3}>
          <Grid.Column textAlign='center'>
            <h1 name={'name'} onChange={this.handleEdit}>{this.props.gig.event.name}</h1>
            <h2 name={'date'} onChange={this.handleEdit}> {weekday[moment(this.props.gig.event.datetime).format('E')]} {moment(this.props.gig.event.datetime).format('MMMM DD YYYY')}</h2>
            <h2 name={'time'} onChange={this.handleEdit}>{moment(this.props.gig.event.datetime).format('h:mm a')}</h2>
            <h2 contenteditable={this.state.editable} name={'location'} >{this.props.gig.event.location}</h2>
            <h2  contenteditable={this.state.editable} name={'notes'} >{this.props.gig.event.notes}</h2>
          </Grid.Column>
          <Grid.Column className={"post-col"}>
           make a different/parallel component here to PostList called GigPostList
          </Grid.Column>
          <Grid.Column textAlign='center'>
          make a different/parallel component here to PlayerList called GigPlayerList
          </Grid.Column>
        </Grid>}
        </React.Fragment>
      )
  }
}


  const mapStateToProps = (state, propsFromParent) => {
    console.log(state.events)
    let gig = state.userEvents.find(gig => gig.id === parseInt(propsFromParent.gigId))
    let event =  state.events.find( event => event.id === gig.id)
    return {
      loading: state.loading,
      events: state.events,
      posts: state.posts,
      gig: gig,
      event: event
    };
   };

   export default connect(
     mapStateToProps,
     { fetchEvents, fetchPosts }
   )(GigDetail);


