import React from "react";

import { Form, Button, Modal } from 'semantic-ui-react'



class NewEventForm extends React.Component{
  constructor(){
    super()
    this.state={
     name: "",
     location: "",
     datetime: "",
     notes: ""
    }
  }

   handleSubmit = (e) => {
      e.preventDefault();
      fetch(`http://localhost:3001/events/`, {
      method: "PATCH",
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`,
        'Content-Type': 'application/json',
        Accept: "application/json"
      },
      body: JSON.stringify({
            name: this.state.name,
            location: this.state.location,
            datetime: this.state.datetime,
            notes: this.state.notes
          })
      })
        .then(r => r.json())
          .then(json => console.log(json))

      //     this.props.dispatch({
      // type:'ADD_EVENT',
      // data});
    }

    handleChange = (e) => {
        this.setState({
          [e.target.name]: e.target.value
        })
      }



render(){
  console.log(this.state)
  return(
    <Modal trigger={<Button> Create New Event </Button>}>
      <Modal.Content>
      <Modal.Header>Create New Event</Modal.Header>
        <Form onSubmit={(e) => this.handleSubmit(e)}>
          <Form.Field>
            <label> Name </label>
            <input name='name' placeholder='Name' onChange={(e)=> this.handleChange(e)} />
          </Form.Field>
          <Form.Field>
            <label>Location</label>
            <input name='location' placeholder='Location' onChange={(e)=> this.handleChange(e)} />
          </Form.Field>
          <Form.Field>
            <label>DateTime</label>
            <input name='date' placeholder='Time' onChange={(e)=> this.handleChange(e)} />
          </Form.Field>
          <Form.Field>
            <label>Notes</label>
            <input name='notes' placeholder='Notes' onChange={(e)=> this.handleChange(e)} />
          </Form.Field>
          <Button type='submit'>Submit</Button>
        </Form>
      </Modal.Content>
  </Modal>
  )
}




}
//
// # Event
// # t.string :name
// # t.date :datetime
// # t.string :location
// # t.string :notes
// # t.integer :user_id


export default NewEventForm;
