import React from 'react';
import { Button, Form } from 'semantic-ui-react'

import { connect } from "react-redux";
import { setCurrentUser } from "../redux/actions/index";

const baseUrl = "http://localhost:3001"

class Login extends React.Component {
  constructor(){
    super()
    this.state = {
      email: "",
      password: ""
    }
  }

    onChange = (e) => {
      this.setState({
      [e.target.name]: ""
      })
    }



    handleSubmit = (e) => {
      e.preventDefault();
      let data = JSON.stringify({
        email: e.target[0].value,
        password: e.target[1].value
      });
      fetch(baseUrl + "/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
            Accept: "application/json"
        },
        body: data
      }).then(res => {
        if (res.status === 401) {
          alert("login failed");
        } else {
          return res.json();
        }
      })
      .then(json => {
        console.log(json.user)
        // this.props.setCurrentUser(json.user);
        this.props.updateUser(json.user);
        localStorage.setItem("token", json.token);
      }); ///this works got a user and a token but did not reroute
      this.setState({
      [e.target.name]: ""
      })
    };

render(){
  console.log(this.state)
  return (
    <div className='ui card login' style={{padding: '12px',
    margin: '0 6px 6px'}}>
    <Form
      onSubmit={this.handleSubmit}>
      <Form.Field>
      <label> Email </label>
      <input name='email' placeholder='email' onChange={()=> this.onChange()} />
      </Form.Field>
      <Form.Field>
      <label>Password</label>
      <input name= 'password' placeholder='Password' onChange={()=>this.onChange()} />
      </Form.Field>
      <Button type='submit'>Submit</Button>
    </Form>
    </div>
  );
  }
}


export default Login;
